import { ImageItem } from '@enact/sandstone/ImageItem';
import { useState } from 'react';
import kind from '@enact/core/kind';

const ProductList = (props) => {
  const products = props.products;
  const [selectedIndex, setSelect] = useState(-1);
  return (
    products.map((p, index) => {
      return <Product product={p} index={index} key={index} isSelected={(selectedIndex == index)} setSelect={setSelect} />;
    })
  );
};

const Product = (props) => {
  const product = props.product;
  const isSelected = props.isSelected;
  const setSelect = props.setSelect;
  return <ImageItem label={product.name} orientation={isSelected ? 'vertical' : 'horizontal'} src={product.image}
    onClick={(e) => {setSelect(isSelected ? -1 : props.index)}}
  >
    <div className="title">

    </div>
    <div className="price">

    </div>    
  </ImageItem>;
};

export default ProductList;
