import { useState } from 'react';
import kind from '@enact/core/kind';

import { ImageItem } from '@enact/sandstone/ImageItem';
import css from './ProductList.module.less';

const ProductList = kind({
  name: 'ProductList',
  styles:{
    css,
    className: 'ProductList',
  },
  render: ({products, css, ...rest}) => (
    <ImageItem></ImageItem>
  )
});

const ProductList_ = (props) => {
  const products = props.products;
  const [selectedIndex, setSelect] = useState(-1);
  return (
    products.map((p, index) => {
      return <ImageItem>

      </ImageItem>
      // return <Product product={p} index={index} key={index} isSelected={(selectedIndex == index)} setSelect={setSelect} />;
    })
  );
};

const ProductItem = kind({
  name: 'ProductItem',
  styles: {
      css,
      className: 'ProductItem',
  },
  render: ({children, css, ...rest}) => (
    <UiButton {...rest} css={css}>{children}</UiButton>
  )
});

const Product = (props) => {
  const product = props.product;
  const isSelected = props.isSelected;
  const setSelect = props.setSelect;
  console.log(product);
  return <ImageItem css={{}} orientation={isSelected ? 'vertical' : 'horizontal'} src={product.image}
    onClick={(e) => {setSelect(isSelected ? -1 : props.index)}}
  >
    <div className="name">
      {product.name}
    </div>
    <div className="price">
      {product.price}
    </div>    
  </ImageItem>;
};

export default ProductList;
