import { useState } from 'react';
import kind from '@enact/core/kind';

import { ImageItemBase, ImageItemDecorator } from '@enact/ui/ImageItem';
import css from './ProductList.module.less';

const ProductList = (props) => {
  const products = props.products;
  const [selectedIndex, setSelect] = useState(-1);
  return (
    products.map((p, index) => {
      return <ProductItem product={p} index={index} key={index} isSelected={(selectedIndex == index)} setSelect={setSelect} />;
    })
  );
};

const ProductItem = ImageItemDecorator(kind({
  name: 'ProductItem',
  styles: {
      css,
      className: 'ProductItem',
  },
  render: ({children, css, ...rest}) => (
    <UiButton {...rest} css={css}>{children}</UiButton>
  )
}));

