import {ImageItem} from '@enact/sandstone/ImageItem';
import {useState} from 'react';
import kind from '@enact/core/kind';

const ProductList = (props) => {
  const products = props.products;
  const [selectedIndex, setSelect] = useState(-1);
  return (
    products.map((p, index)=>{
      return <Product product={p} key={index} isSelected={(selectedIndex == index)} setSelect={setSelect} />;
    })
  );
}; 

const Product = (props) => {
  console.log(props.product);
  const product = props.product;
  const isSelected = props.isSelected;
  const setSelect = props.setSelect;
  return <ImageItem label={product.name} orientation='horizontal' src={product.image} 
    selectionComponent={selectionComponent} 
  />;
};

const selectionComponent = kind({
  render: () => <div>Selected component</div>
});

export default ProductList;
